import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'recommendation'
})
export class RecommendationPipe implements PipeTransform {

  transform(value: number, stockpurchaseprice:number,stockcurrentprice:number ) {
      var investmentval = (value*stockpurchaseprice);
      var currentval = (value*stockpurchaseprice);
      var profitpercent=0; 
      var losspercent=0;
      if( investmentval< currentval){
        profitpercent = ((currentval-investmentval)/investmentval)*100;        
      }
      else
        losspercent = ((investmentval-currentval)/investmentval)*100;

      if(losspercent>20){
        return "BuyMore";
      }
      else if((profitpercent>0 && profitpercent<=15) || (losspercent>0 && losspercent<=15) ){
        return "Hold";
      }
      else if(profitpercent>20 || losspercent>40){
        return "Sell";
      }

  }

}
